export default {
  colors: {
    text: '#000',
    background: '#fff',
    primary: '#33e',
  },
  ...
}

export const theme = {
    colors: {
        primary: 'blue',
        secondary: 'red',
    }
}

export const Component = () => (
<div 
sx={{   width: "100px", 
        height: "100px", 
        backgroundColor: "primary"
    }} 
/>)


// layout.js
import { ThemeProvider } from 'theme-ui'
import theme from './theme'
export default props => (
  <ThemeProvider theme={theme}>{props.children}</ThemeProvider>
)

export default {
  buttons: {
    primary: {
      backgroundColor: "primary",
      padding: 4,
      margin: 4
    },
    secondary: {
      backgroundColor: "secondary",
      padding: 2,
      margin: 2
    }
  }
  ...
}

const Header = ({ children }) => (
  <Flex
    sx={{
      alignItems: "center",
      justifyContent: "center",
      padding: 4,
      margin: 4,
      color: "secondary",
      border: theme => `1px solid ${theme.colors.primary}`
    }}
  >
    {children}
  </Flex>
);

  styles: {
    h1: {
      fontSize: 32,
      fontFamily: 'heading',
      fontWeight: 'heading',
      color: 'primary',
      mt: 4,
      mb: 2,
    },
  }
}

